/* #define DEBUG */

class Solution
{
public:
    int jump(vector<int> &nums)
    {
        int len = nums.size();
        int dp[len];
        for (int i = 0; i < len; i++)
        {
            dp[i] = len + 1;
        }
        dp[0] = 0;
        for (int i = 1; i < len; i++)
        {
            for (int j = 0; j < i; j++)
            {
                if (nums[j] >= i - j)
                {
                    dp[i] = dp[i] > dp[j] + 1 ? dp[j] + 1 : dp[i];
                }
            }
        }
        return dp[len - 1];
    }
};

